section.programlisting:
#include <iostream>

class Soldier
{
public:
    std::string name;
    int HP;
    int damage;

    Soldier(std::string name, int HP, int damage) {

        this->name = name;
        this->HP = HP;
        this->damage = damage;
    }

    bool alive() {
        return HP > 0;
    }

    friend std::ostream &operator<< (std::ostream &stream, Soldier &Soldier) {
        stream << Soldier.name << " " << Soldier.HP << " " << Soldier.damage;
        return stream;
    }

};


Soldier &fight(Soldier &first, Soldier &second)
{
    std::cout << "fight func: fight start, " << first << " vs. " << second << std::endl;

    for (; first.alive() && second.alive() ;) {

        first.HP = first.HP - second.damage;
        second.HP = second.HP - first.damage;

        std::cout << "fight func: " << first << std::endl;
        std::cout << "fight func: " << second << std::endl;

    }

    if (first.HP > second.HP)
        return first;
    else
        return second;
}


int main()
{

    Soldier me {"Nandi", 100, 10};
    Soldier against {"Barbarian", 80, 5};
    Soldier third {"Giant", 180, 20};

    std::cout << "main func: " << fight(fight(me, against), fight(me, third)) << std::endl;

    return 0;
}

int main()
{

    Soldier me {"Nandi", 100, 10};
    Soldier against {"Barbarian", 80, 5};
    Soldier third {"Giant", 180, 20};

    Soldier firstWinner = fight(me, against);
    Soldier secondWinner = fight(me, third);

    std::cout << "main func: " << fight(firstWinner, secondWinner) << std::endl;

    return 0;
}

Soldier me {"Matyi", 1000, 100};
Soldier against {"Greta", 800, 50};
Soldier third {"Nandi", 800, 50};

Soldier &firstWinner = fight(me, against);
Soldier &secondWinner = fight(me, third);

std::cout << "main func: " << fight(firstWinner, secondWinner) << std::endl;
